var now = new Date();
function createtime() {
    var t = new Date("09/08/2024 00:00:00");
    now.setTime(now.getTime() + 250);
    var e = (now - t) / 1e3 / 60 / 60 / 24,
        a = Math.floor(e),
        n = (now - t) / 1e3 / 60 / 60 - 24 * a,
        r = Math.floor(n);
    1 == String(r).length && (r = "0" + r);
    var s = (now - t) / 1e3 / 60 - 1440 * a - 60 * r,
        i = Math.floor(s);
    1 == String(i).length && (i = "0" + i);
    var o = (now - t) / 1e3 - 86400 * a - 3600 * r - 60 * i,
        l = Math.round(o);
    1 == String(l).length && (l = "0" + l);

    // 显示站点运行时间和附加句子
    let g = `<span class='textTip'> <br> 本站已经平安运行了 ${a} 天</span><span id='runtime'> ${r} 小时 ${i} 分 ${l} 秒 </span> <i class='fas fa-heartbeat' style='color:red'></i><br><br>
    <span class='quote'>发挥才智，则锋芒毕露。凭借感情，则流于世俗。坚持己见，则多方掣肘。 总之，人世难居。—夏目漱石《草枕》</span>`;

    document.getElementById("workboard") && (document.getElementById("workboard").innerHTML = g);
}
setInterval(() => { createtime(); }, 250);
